mapper.select:
select id,RealName,Sex,rgdate,Course,Teacher,Classnum,phone,Fee,(select SUM(case when Company =#{c} and realname=#{s.realName} then paid + money else 0 end) from payment ) mall ,Fee -Cost as Nocost,(select SUM(case when Company=#{c} and student_name=#{s.realName} and course=#{s.course} then keshi else 0 end ) from keshi_detail ) nall,Allhour - Hour as Nohour,Allhour,Type FROM student
where Company =#{c}

	and Realname like "%"#{d}"%"


	and rgdate between #{a} and #{b}


	and Teacher like "%"#{E}"%"


	and Course like "%"#{f}"%"

SELECT s.id, s.RealName, s.Sex, s.rgdate, s.Course, s.Teacher, s.Classnum, s.phone, s.Fee, ISNULL(p.mall, 0) as mall, s.Fee - s.Cost as Nocost, ISNULL(k.nall, 0) as nall, s.Allhour - s.Hour as Nohour, s.Allhour, s.Type FROM student s LEFT JOIN ( SELECT realname, ISNULL(SUM(paid + money), 0) as mall FROM payment WHERE Company = #{c} AND realname = #{s.realName} GROUP BY realname ) p ON s.RealName = p.realname LEFT JOIN ( SELECT student_name, course, ISNULL(SUM(keshi), 0) as nall FROM keshi_detail WHERE Company = #{c} AND student_name = #{s.realName} AND course = #{s.course} GROUP BY student_name, course ) k ON s.RealName = k.student_name AND s.Course = k.course WHERE s.Company = #{c}
and s.Realname LIKE '%' + #{d} + '%'
and s.rgdate between #{a} and #{b}
and s.Teacher LIKE '%' + #{E} + '%'
and s.Course LIKE '%' + #{f} + '%'

select id,RealName,Sex,rgdate,Course,Teacher,Classnum,phone,Fee,(select SUM(case when Company =#{c} and realname=student.realname then paid + money else 0 end) from payment ) mall ,Fee -(select SUM(case when Company =#{c} and realname=student.realname then paid + money else 0 end) from payment ),(select SUM(case when Company=#{c} and student_name=student.realname and course=student.Course then keshi else 0 end ) from keshi_detail ) nall,Allhour - (select SUM(case when Company=#{c} and student_name=student.realname and course=student.Course then keshi else 0 end ) from keshi_detail ) as Nohour,Allhour,Type FROM student where Company =#{c}
and Realname like "%"#{d}"%"
and rgdate between #{a} and #{b}
and Teacher like "%"#{E}"%"
and Course like "%"#{f}"%"
order by id limit #{page},10

SELECT * FROM ( SELECT s.id, s.RealName, s.Sex, s.rgdate, s.Course, s.Teacher, s.Classnum, s.phone, s.Fee, ISNULL(p.mall, 0) as mall, s.Fee - ISNULL(p.mall, 0) as nocost, ISNULL(k.nall, 0) as nall, s.Allhour - ISNULL(k.nall, 0) as Nohour, s.Allhour, s.Type, ROW_NUMBER() OVER (ORDER BY s.id) as row_num FROM student s LEFT JOIN ( SELECT realname, ISNULL(SUM(paid + money), 0) as mall FROM payment WHERE Company = #{c} GROUP BY realname ) p ON s.RealName = p.realname LEFT JOIN ( SELECT student_name, course, ISNULL(SUM(keshi), 0) as nall FROM keshi_detail WHERE Company = #{c} GROUP BY student_name, course ) k ON s.RealName = k.student_name AND s.Course = k.course WHERE s.Company = #{c}
and s.Realname LIKE '%' + #{d} + '%'
and s.rgdate between #{a} and #{b}
and s.Teacher LIKE '%' + #{E} + '%'
and s.Course LIKE '%' + #{f} + '%'
) AS temp WHERE row_num BETWEEN #{page} + 1 AND #{page} + 10

mapper.update:
update student set RealName=#{s.realName}, Sex=#{s.sex}, rgdate=
#{s.rgdate}
null
, Course=#{s.course}, Teacher=#{s.teacher}, Classnum=#{s.classnum}, phone=#{s.phone}, Fee=#{s.fee}, Cost=#{s.cost}, Nocost=#{s.nocost}, Hour=#{s.hour}, Nohour=#{s.nohour}, Allhour=#{s.allHour}, Type=#{s.type} where ID=#{s.id} and Company =#{c}

mapper.select:
select * from student where Company =#{c}
and RealName like "%"#{a}"%"
and RealName = null

select * from student where Company =#{c}
and RealName LIKE '%' + #{a} + '%'
and RealName = null
