mapper.select:
select id,RealName,Sex,rgdate,Course,Teacher,Classnum,phone,Fee,(select SUM(case when Company =#{c} and realname=#{s.realName} then paid else 0 end) from payment ) mall ,Fee -Cost as Nocost,(select SUM(case when Company=#{c} and student_name=#{s.realName} and course=#{s.course} then keshi else 0 end ) from keshi_detail ) nall,Allhour - Hour as Nohour,Allhour,Type FROM student
where Company =#{c}

	and Realname like "%"#{d}"%"


	and rgdate between #{a} and #{b}


	and Teacher like "%"#{E}"%"


	and Course like "%"#{f}"%"

select id,RealName,Sex,rgdate,Course,Teacher,Classnum,phone,Fee,(select SUM(case when Company =#{c} and realname=student.realname then paid else 0 end) from payment ) mall ,Fee -Cost as Nocost,(select SUM(case when Company=#{c} and student_name=student.realname and course=student.Course then keshi else 0 end ) from keshi_detail ) nall,Allhour - Hour as Nohour,Allhour,Type FROM student where Company =#{c}
and Realname like "%"#{d}"%"
and rgdate between #{a} and #{b}
and Teacher like "%"#{E}"%"
and Course like "%"#{f}"%"
order by id limit #{page},10

select * from student
where Nocost is not null and Nocost>0 and Company =#{c}

	and Realname like "%"#{a}"%"

mapper.update:
update student set RealName=#{s.realName}, Sex=#{s.sex}, rgdate=
#{s.rgdate}
null
, Course=#{s.course}, Teacher=#{s.teacher}, Classnum=#{s.classnum}, phone=#{s.phone}, Fee=#{s.fee}, Cost=#{s.cost}, Nocost=#{s.nocost}, Hour=#{s.hour}, Nohour=#{s.nohour}, Allhour=#{s.allHour}, Type=#{s.type} where ID=#{s.id} and Company =#{c}

mapper.select:
select * from student where Company =#{c}
and RealName like "%"#{a}"%"
and RealName = null
