mapper.select:
select * from keshi_detail where Company =#{c}

	and teacher_name like "%"#{a}"%"


	and course like "%"#{b}"%"


	and riqi between #{d} and #{f}

select * from keshi_detail where Company =#{c}

	and teacher_name LIKE '%' + #{a} + '%'


	and course LIKE '%' + #{b} + '%'


	and riqi between #{d} and #{f}

select * from keshi_detail where Company =#{c}


	and teacher_name like "%"#{a}"%"


	and course like "%"#{b}"%"


	and riqi between #{d} and #{f}

order by id limit #{page},10

SELECT * FROM (
SELECT *, ROW_NUMBER() OVER (ORDER BY id) as row_num
FROM keshi_detail
WHERE Company = #{c}

	and teacher_name LIKE '%' + #{a} + '%'


	and course LIKE '%' + #{b} + '%'


	and riqi between #{d} and #{f}

) AS temp
WHERE row_num BETWEEN #{page} + 1 AND #{page} + 10

mapper.update:
update keshi_detail set riqi=
#{ksd.riqi}
null
, student_name=#{ksd.student_name}, course=#{ksd.course}, keshi = #{ksd.keshi}, teacher_name=#{ksd.teacher_name}, jine=#{ksd.jine} where id=#{ksd.id} and Company =#{c}

mapper.select:
select student_name,teacher_name,course,sum(keshi) as keshi , MIN(riqi) as riqi from keshi_detail where Company = #{c}

	and teacher_name  like "%"#{a}"%"


	and riqi between #{d} and #{f}

group by teacher_name,course,jine order by teacher_name

select student_name,teacher_name,course,sum(keshi) as keshi from keshi_detail where Company = #{c}

	and teacher_name  LIKE '%' + #{a} + '%'


	and riqi between #{d} and #{f}

group by teacher_name,course,jine order by teacher_name
