mapper.select:
select * from keshi_detail where Company =#{c}

	and teacher_name like "%"#{a}"%"


	and course like "%"#{b}"%"


	and riqi between #{d} and #{f}

select * from keshi_detail where Company =#{c}


	and teacher_name like "%"#{a}"%"


	and course like "%"#{b}"%"


	and riqi between #{d} and #{f}

order by id limit #{page},10

mapper.update:
update keshi_detail set riqi=
#{ksd.riqi}
null
, student_name=#{ksd.student_name}, course=#{ksd.course}, teacher_name=#{ksd.teacher_name}, jine=#{ksd.jine} where id=#{ksd.id} and Company =#{c}

mapper.select:
select teacher_name,course,sum(keshi) as keshi,jine,sum(jine*keshi) as gongzi from keshi_detail where Company =#{c}

	and teacher_name  like "%"#{a}"%"


	and riqi between #{d} and #{f}

group by teacher_name,jine order by teacher_name

select teacher_name,course,sum(keshi) as keshi,jine,sum(jine*keshi) as gongzi from keshi_detail where Company =#{c}

	and riqi  like "%"#{a}"%"

group by teacher_name,jine order by teacher_name
